mapper.delete:
       UPDATE order_tbl
		  SET order_show = 'N'
		WHERE order_id = #{order_id};

mapper.select:
SELECT ot.order_date AS order_date, ot.e_id AS e_id, ct.client_code AS client_code, ct.client_name AS client_name, it.item_code AS item_code, it.item_name AS item_name, oit.quantity, oit.price, oit.total AS total, ot.transaction_type AS transaction_type FROM order_tbl ot LEFT JOIN order_item_tbl oit ON ot.order_id = oit.order_id LEFT JOIN item_tbl it ON oit.item_id = it.item_id LEFT JOIN client_tbl ct ON ot.client_code = ct.client_code
ot.order_show = 'Y'
AND ot.order_date BETWEEN #{start_date} AND #{end_date}
AND ot.client_code = #{client_code}
AND ot.e_id = #{e_id}
AND ot.storage_code = #{storage_code}
AND it.item_code = #{item_code}
AND ot.transaction_type = #{transaction_type}
